import React, { useState } from 'react';

// Main App component
function App() {
    // State variables for managing input and results
    const [code, setCode] = useState('');
    const [language, setLanguage] = useState('cpp');
    const [loading, setLoading] = useState(false);
    const [results, setResults] = useState(null); // Stores the analysis results (complexity, optimizations, identified problem)
    const [alternativeImplementations, setAlternativeImplementations] = useState([]); // Initialize as empty array to show section
    const [youtubeVideos, setYoutubeVideos] = useState([]); // Initialize as empty array to show section
    const [error, setError] = useState(null); // Stores any error messages

    // Function to handle code analysis when the button is clicked
    const handleAnalyzeCode = async () => {
        // Basic validation for code input
        if (!code.trim()) {
            // Using a custom message box instead of alert()
            showMessageBox('Please enter some code to analyze.');
            return;
        }

        // Reset previous results and errors, show loading indicator
        setResults(null);
        setAlternativeImplementations([]); // Reset to empty array
        setYoutubeVideos([]); // Reset to empty array
        setError(null);
        setLoading(true);

        try {
            // Make a POST request to the backend server
            // Ensure this URL matches where your Node.js backend is running
            const response = await fetch('http://localhost:3000/analyze-code', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ code, language }),
            });

            // Check if the response was successful
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'Something went wrong on the server.');
            }

            // Parse the JSON response from the backend
            const data = await response.json();
            console.log('Received data:', data);

            // Update the results state with the fetched data
            setResults({
                time_complexity: data.time_complexity,
                time_complexity_explanation: data.time_complexity_explanation,
                space_complexity: data.space_complexity,
                space_complexity_explanation: data.space_complexity_explanation,
                optimization_suggestions: data.optimization_suggestions,
                identified_problem: data.identified_problem
            });
            // Set alternative implementations if provided by the backend, otherwise keep empty array
            setAlternativeImplementations(data.alternative_implementations || []);
            // Set YouTube videos if provided by the backend, otherwise keep empty array
            setYoutubeVideos(data.youtube_videos || []);

        } catch (err) {
            console.error('Error during analysis:', err);
            setError('Failed to analyze code: ' + err.message);
            // Using a custom message box for errors
            showMessageBox('Failed to analyze code: ' + err.message);
        } finally {
            // Hide loading indicator regardless of success or failure
            setLoading(false);
        }
    };

    // Custom message box function (instead of alert)
    const showMessageBox = (message) => {
        const messageBox = document.createElement('div');
        messageBox.style.cssText = `
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background-color: #fff;
            padding: 20px;
            border-radius: 10px;
            box-shadow: 0 5px 15px rgba(0,0,0,0.3);
            z-index: 1000;
            text-align: center;
            font-family: 'Inter', sans-serif;
            color: #333;
            max-width: 80%;
        `;
        messageBox.innerHTML = `
            <p>${message}</p>
            <button style="
                margin-top: 15px;
                padding: 8px 15px;
                background-color: #3b82f6;
                color: white;
                border: none;
                border-radius: 5px;
                cursor: pointer;
            ">OK</button>
        `;
        document.body.appendChild(messageBox);

        messageBox.querySelector('button').onclick = () => {
            document.body.removeChild(messageBox);
        };
    };


    return (
        // The main container for the application, with styling replicated from HTML
        <div className="container">
            {/* Custom CSS for the application, embedded directly for simplicity */}
            {/* In a real React project, this would ideally be in a separate .css file and imported */}
            <style>
                {`
                body {
                    /* These body styles are actually applied by public/index.html */
                    /* but kept here for completeness if this component were standalone */
                    font-family: 'Inter', sans-serif;
                    background-color: #f3f4f6; /* Light gray background */
                    display: flex;
                    justify-content: center;
                    align-items: flex-start; /* Align to top */
                    min-height: 100vh;
                    padding: 2rem;
                    box-sizing: border-box;
                }
                .container {
                    background-color: #ffffff;
                    border-radius: 1rem; /* Rounded corners */
                    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
                    padding: 2.5rem;
                    width: 100%;
                    max-width: 960px; /* Max width for larger screens */
                    display: flex;
                    flex-direction: column;
                    gap: 2rem;
                }
                h1 {
                    font-size: 2.25rem; /* text-4xl */
                    font-weight: 700; /* font-bold */
                    color: #1f2937; /* Gray-900 */
                    text-align: center;
                    margin-bottom: 1.5rem;
                }
                .section-title {
                    font-size: 1.5rem; /* text-2xl */
                    font-weight: 600; /* font-semibold */
                    color: #1f2937;
                    margin-bottom: 1rem;
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                }
                textarea {
                    min-height: 200px;
                    resize: vertical;
                }
                button {
                    transition: background-color 0.2s ease-in-out, transform 0.1s ease-in-out;
                }
                button:hover {
                    transform: translateY(-2px);
                }
                button:active {
                    transform: translateY(0);
                }
                .result-box {
                    background-color: #f9fafb; /* Lightest gray */
                    border: 1px solid #e5e7eb; /* Border color */
                    border-radius: 0.75rem; /* Rounded corners */
                    padding: 1.5rem;
                }
                .complexity-item {
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                    margin-bottom: 0.5rem;
                }
                .complexity-tag {
                    background-color: #d1fae5; /* Green-100 */
                    color: #065f46; /* Green-800 */
                    padding: 0.25rem 0.75rem;
                    border-radius: 9999px; /* Full rounded */
                    font-weight: 600;
                    font-size: 0.875rem; /* text-sm */
                }
                .video-card {
                    background-color: #ffffff;
                    border: 1px solid #e5e7eb;
                    border-radius: 0.75rem;
                    padding: 1rem;
                    display: flex;
                    gap: 1rem;
                    align-items: center;
                    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
                    transition: transform 0.2s ease-in-out;
                }
                .video-card:hover {
                    transform: translateY(-3px);
                    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
                }
                .video-thumbnail {
                    width: 120px;
                    height: 90px;
                    border-radius: 0.5rem;
                    object-fit: cover;
                }
                .video-info h3 {
                    font-weight: 600;
                    color: #1f2937;
                    margin-bottom: 0.25rem;
                    font-size: 1rem;
                }
                .video-info p {
                    font-size: 0.875rem;
                    color: #6b7280;
                    margin-bottom: 0.5rem;
                }
                .watch-button {
                    display: inline-flex;
                    align-items: center;
                    gap: 0.25rem;
                    background-color: #ef4444; /* Red-500 */
                    color: white;
                    padding: 0.5rem 1rem;
                    border-radius: 0.5rem;
                    font-weight: 500;
                    font-size: 0.875rem;
                    text-decoration: none;
                }
                .watch-button:hover {
                    background-color: #dc2626; /* Red-600 */
                }
                #loading-indicator {
                    /* display property controlled by React state */
                    text-align: center;
                    margin-top: 1rem;
                    font-weight: 500;
                    color: #4b5563;
                }
                .code-block {
                    background-color: #1f2937; /* Dark background for code */
                    color: #e5e7eb; /* Light text color */
                    padding: 1rem;
                    border-radius: 0.5rem;
                    font-family: 'SFMono-Regular', Consolas, 'Liberation Mono', Menlo, Courier, monospace;
                    font-size: 0.875rem;
                    overflow-x: auto; /* Enable horizontal scrolling for long lines */
                }

                /* SVG Icons for section titles */
                .icon {
                    width: 1.5rem;
                    height: 1.5rem;
                    fill: currentColor; /* Use current text color */
                }
                `}
            </style>

            {/* Application Header */}
            <h1 className="flex items-center justify-center gap-2">
                <svg className="icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                    <path d="M10.707 17.707a1 1 0 001.414 0l4-4a1 1 0 000-1.414l-4-4a1 1 0 00-1.414 1.414L13.586 12l-2.879 2.879a1 1 0 000 1.414zM7 21a1 1 0 01-1-1V4a1 1 0 011-1h10a1 1 0 011 1v3a1 1 0 11-2 0V5H8v14h9v-2a1 1 0 112 0v2a1 1 0 01-1 1H7z"/>
                </svg>
                CodeAnalyzer
            </h1>

            {/* Introductory Section */}
            <div className="bg-blue-50 p-6 rounded-xl text-center">
                <h2 className="text-xl font-semibold text-blue-800 mb-2">Analyze Your Code with AI</h2>
                <p className="text-blue-700">Get instant insights into time & space complexity, optimization suggestions, and curated learning resources for any programming language.</p>
            </div>

            {/* Language Selection Input */}
            <div className="input-section">
                <label htmlFor="language-select" className="block text-gray-700 text-sm font-bold mb-2">Select Language</label>
                <select
                    id="language-select"
                    className="shadow appearance-none border rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    value={language}
                    onChange={(e) => setLanguage(e.target.value)}
                >
                    <option value="cpp">C++</option>
                    <option value="java">Java</option>
                    <option value="python">Python</option>
                    <option value="javascript">JavaScript</option>
                    <option value="typescript">TypeScript</option>
                    <option value="csharp">C#</option>
                    <option value="go">Go</option>
                    <option value="rust">Rust</option>
                    <option value="php">PHP</option>
                    <option value="ruby">Ruby</option>
                </select>
            </div>

            {/* Code Input Textarea */}
            <div className="input-section">
                <label htmlFor="code-input" className="block text-gray-700 text-sm font-bold mb-2">Code Input</label>
                <textarea
                    id="code-input"
                    className="shadow appearance-none border rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Paste your code here..."
                    value={code}
                    onChange={(e) => setCode(e.target.value)}
                ></textarea>
            </div>

            {/* Analyze Button */}
            <button
                id="analyze-button"
                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 shadow-lg flex items-center justify-center gap-2"
                onClick={handleAnalyzeCode}
                disabled={loading} // Disable button when loading
            >
                <svg className="icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                    <path fillRule="evenodd" d="M11.47 2.47a.75.75 0 011.06 0l3.75 3.75a.75.75 0 01-1.06 1.06L12 4.06V16.5a.75.75 0 01-1.5 0V4.06L8.78 7.28a.75.75 0 01-1.06-1.06l3.75-3.75zM4.5 19.5a.75.75 0 01.75-.75h14.5a.75.75 0 010 1.5H5.25a.75.75 0 01-.75-.75z" clipRule="evenodd" />
                </svg>
                {loading ? 'Analyzing...' : 'Analyze with AI'}
            </button>

            {/* Loading Indicator */}
            {loading && (
                <div id="loading-indicator" style={{ display: 'block' }}>Analyzing code... Please wait.</div>
            )}

            {/* Error Message */}
            {error && (
                <div className="text-red-600 bg-red-100 p-4 rounded-lg mt-4">
                    <p className="font-semibold">Error:</p>
                    <p>{error}</p>
                </div>
            )}

            {/* Main Results Section (Always visible, content changes) */}
            <div id="results-section">
                {/* Complexity Analysis Section */}
                <div className="complexity-analysis">
                    <h2 className="section-title">
                        <svg className="icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                            <path fillRule="evenodd" d="M12 2.25c-5.385 0-9.75 4.365-9.75 9.75s4.365 9.75 9.75 9.75 9.75-4.365 9.75-9.75S17.385 2.25 12 2.25zM12.75 6a.75.75 0 00-1.5 0v6c0 .414.336.75.75.75h4.5a.75.75 0 000-1.5h-3.75V6z" clipRule="evenodd" />
                        </svg>
                        Complexity Analysis
                    </h2>
                    <div className="result-box">
                        <div className="complexity-item">
                            <span className="font-semibold text-gray-800">Time Complexity:</span>
                            <span id="time-complexity" className="complexity-tag">{results?.time_complexity || 'N/A'}</span>
                        </div>
                        <div className="complexity-item">
                            <span className="font-semibold text-gray-800">Space Complexity:</span>
                            <span id="space-complexity" className="complexity-tag">{results?.space_complexity || 'N/A'}</span>
                        </div>
                        <p id="complexity-explanation" className="text-gray-700 mt-2">{results?.complexity_explanation || 'No explanation available.'}</p>
                    </div>
                </div>

                {/* Optimization Suggestions Section */}
                <div className="optimization-suggestions">
                    <h2 className="section-title">
                        <svg className="icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                            <path fillRule="evenodd" d="M12 1.5a5.25 5.25 0 00-5.25 5.25v3a3 3 0 00-3 3v6.75a3 3 0 003 3h10.5a3 3 0 003-3v-6.75a3 3 0 00-3-3v-3c0-2.9-2.355-5.25-5.25-5.25zm3.75 8.25v-3a2.25 2.25 0 00-4.5 0v3h4.5z" clipRule="evenodd" />
                        </svg>
                        Optimization Suggestions
                    </h2>
                    <div className="result-box">
                        <ul id="optimization-list" className="list-disc list-inside text-gray-700">
                            {results?.optimization_suggestions && results.optimization_suggestions.length > 0 ? (
                                results.optimization_suggestions.map((suggestion, index) => (
                                    <li key={index}>{suggestion}</li>
                                ))
                            ) : (
                                <li>No specific optimization suggestions at this time.</li>
                            )}
                        </ul>
                    </div>
                </div>

                {/* Alternative Implementations Section */}
                <div className="alternative-implementations">
                    <h2 className="section-title">
                        <svg className="icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                            <path fillRule="evenodd" d="M10.707 17.707a1 1 0 001.414 0l4-4a1 1 0 000-1.414l-4-4a1 1 0 00-1.414 1.414L13.586 12l-2.879 2.879a1 1 0 000 1.414zM7 21a1 1 0 01-1-1V4a1 1 0 011-1h10a1 1 0 011 1v3a1 1 0 11-2 0V5H8v14h9v-2a1 1 0 112 0v2a1 1 0 01-1 1H7z" clipRule="evenodd" />
                        </svg>
                        Alternative Implementations
                    </h2>
                    <div className="grid grid-cols-1 gap-4">
                        {alternativeImplementations && alternativeImplementations.length > 0 ? (
                            alternativeImplementations.map((alt, index) => (
                                <div key={index} className="result-box">
                                    <h3 className="font-semibold text-gray-800 mb-2">{alt.title}</h3>
                                    <pre className="code-block"><code>{alt.code}</code></pre>
                                </div>
                            ))
                        ) : (
                            <p className="text-gray-700 result-box">No alternative implementations available yet. Analyze some code to see suggestions!</p>
                        )}
                    </div>
                </div>

                {/* Recommended Videos Section */}
                <div className="recommended-videos">
                    <h2 className="section-title">
                        <svg className="icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                            <path fillRule="evenodd" d="M4.5 5.653c0-1.426 1.529-2.33 2.779-1.643l11.54 6.348c1.295.712 1.295 2.566 0 3.278L7.28 20.32c-1.25.687-2.779-.201-2.779-1.643V5.653z" clipRule="evenodd" />
                        </svg>
                        Recommended Videos
                    </h2>
                    <div id="video-list" className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        {youtubeVideos && youtubeVideos.length > 0 ? (
                            youtubeVideos.map((video, index) => (
                                <div key={index} className="video-card">
                                    <img src={video.thumbnail || 'https://placehold.co/120x90/e5e7eb/6b7280?text=No+Thumb'} alt={video.title} className="video-thumbnail" />
                                    <div className="video-info">
                                        <h3>{video.title}</h3>
                                        <p className="text-gray-600">by {video.channelTitle}</p>
                                        <a href={video.url} target="_blank" rel="noopener noreferrer" className="watch-button">
                                            <svg className="icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" style={{ width: '1rem', height: '1rem' }}>
                                                <path fillRule="evenodd" d="M4.5 5.653c0-1.426 1.529-2.33 2.779-1.643l11.54 6.348c1.295.712 1.295 2.566 0 3.278L7.28 20.32c-1.25.687-2.779-.201-2.779-1.643V5.653z" clipRule="evenodd" />
                                            </svg>
                                            Watch Video
                                        </a>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p className="text-gray-700 result-box">No relevant videos found yet. Analyze some code to get recommendations!</p>
                        )}
                    </div>
                </div>
            </div>

            {/* Footer Section (Informational Cards) */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-8">
                <div className="bg-white p-6 rounded-xl shadow-md text-center flex flex-col items-center gap-2">
                    <svg className="icon text-blue-600" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                        <path fillRule="evenodd" d="M12 2.25c-5.385 0-9.75 4.365-9.75 9.75s4.365 9.75 9.75 9.75 9.75-4.365 9.75-9.75S17.385 2.25 12 2.25zm-2.625 7.5a.75.75 0 100 1.5h.375a.75.75 0 100-1.5H9.375zM12 10.5a.75.75 0 100 1.5h.008a.75.75 0 100-1.5H11.25zM15 15.75a.75.75 0 100 1.5h.008a.75.75 0 100-1.5H15zm2.625 0a.75.75 0 100 1.5h.375a.75.75 0 100-1.5h-.375z" clipRule="evenodd" />
                    </svg>
                    <h3 className="font-semibold text-lg text-gray-800">Instant Analysis</h3>
                    <p className="text-gray-600 text-sm">Get real-time complexity analysis powered by advanced AI.</p>
                </div>
                <div className="bg-white p-6 rounded-xl shadow-md text-center flex flex-col items-center gap-2">
                    <svg className="icon text-green-600" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                        <path fillRule="evenodd" d="M12 1.5a5.25 5.25 0 00-5.25 5.25v3a3 3 0 00-3 3v6.75a3 3 0 003 3h10.5a3 3 0 003-3v-6.75a3 3 0 00-3-3v-3c0-2.9-2.355-5.25-5.25-5.25zm3.75 8.25v-3a2.25 2.25 0 00-4.5 0v3h4.5z" clipRule="evenodd" />
                    </svg>
                    <h3 className="font-semibold text-lg text-gray-800">Smart Suggestions</h3>
                    <p className="text-gray-600 text-sm">Receive personalized optimization recommendations.</p>
                </div>
                <div className="bg-white p-6 rounded-xl shadow-md text-center flex flex-col items-center gap-2">
                    <svg className="icon text-red-600" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                        <path fillRule="evenodd" d="M4.5 5.653c0-1.426 1.529-2.33 2.779-1.643l11.54 6.348c1.295.712 1.295 2.566 0 3.278L7.28 20.32c-1.25.687-2.779-.201-2.779-1.643V5.653z" clipRule="evenodd" />
                    </svg>
                    <h3 className="font-semibold text-lg text-gray-800">Learn More</h3>
                    <p className="text-gray-600 text-sm">Discover curated YouTube videos to deepen your understanding.</p>
                </div>
            </div>
        </div>
    );
}

export default App;
